sysauto_script.script:
// Get the current time
var rightNow = new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values within 24 hours after the current time. Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var dueInADay = new GlideRecord('x_58872_needit_needit_task');
dueInADay.addQuery('due_date', '<=', gs.hoursAgo(-24));
dueInADay.addQuery('due_date', '>=', rightNow);
dueInADay.addQuery('state','!=',3);
dueInADay.query();

// Write a log message for each NeedIt Task Record that is due in the next 24 hours
while (dueInADay.next()) {
    gs.info("Due in a day NeedIt Task Record = " + dueInADay.number);
	gs.eventQueue("x_58872_needit.dueSoonNeedItTasks", dueInADay, dueInADay.number, gs.getUserName());
}
